mapper.select:
		Select * from (
			select ROWNUM numrow, aa.*
			from (
				select *
				from musicals
				where sysdate > START_DATE and sysdate < END_DATE
                ORDER BY MUSICALS.START_DATE
                ) aa
		)

		Select * from (
			select ROWNUM numrow, aa.*
			from (
				select *
				from musicals
				where sysdate > END_DATE
                ORDER BY MUSICALS.START_DATE
                ) aa
		)

		Select * from (
			select ROWNUM numrow, aa.*
			from (
				select *
				from musicals
				where sysdate < START_DATE
                ORDER BY MUSICALS.START_DATE
                ) aa
		)
